mapper.select:
select t_code , cls_code , get_cd_nm(t_code || cls_code, '') AS ins_nm , lq_value , gr_value , rs_value , ita_rec , lq_rec , corp_cnt , worker_cnt from ciams_industry_analysis where sgg_cd = (SELECT sido || sgg FROM (SELECT (SELECT conf_value FROM ciams_config WHERE id = 'SIDO_CODE') AS sido, (SELECT conf_value FROM ciams_config WHERE id = 'SGG_CODE') AS sgg) config)
AND ita_rec = #{type}
AND lq_rec = #{type}
AND 1 = 0
order by t_code, cls_code

SELECT t1.t_code, t1.cls_code, get_cd_nm(t1.t_code || t1.cls_code, '') AS ins_nm, t1.ita_rec, t1.lq_rec, t2.corp_name, t2.location, t2.worker_cnt FROM ciams_industry_rep t2 INNER JOIN ( SELECT t_code, cls_code, ita_rec, lq_rec FROM ciams_industry_analysis where sgg_cd = ( SELECT sido || sgg FROM ( SELECT (SELECT conf_value FROM ciams_config WHERE id = 'SIDO_CODE') AS sido, (SELECT conf_value FROM ciams_config WHERE id = 'SGG_CODE') AS sgg ) config )
AND ita_rec = #{type}
AND lq_rec = #{type}
AND 1 = 0
) t1 ON t2.t_code = t1.t_code AND t2.cls_code = t1.cls_code order by worker_cnt desc
